interface Hospital {
  id: string;
  name: string;
  city: string;
  district: string;
  address: string;
  phone: string;
  email: string;
  contactPerson: string;
  isActive: boolean;
  logoUrl?: string;
  userCount?: number;
}

interface TurkeyMapProps {
  hospitals: Hospital[];
  onCityHover?: (city: string, hospitals: Hospital[]) => void;
  onCityClick?: (city: string, hospitals: Hospital[]) => void;
}

const TurkeyMap: React.FC<TurkeyMapProps> = ({ hospitals, onCityHover, onCityClick }) => {
  const [hoveredCity, setHoveredCity] = useState<string | null>(null);
  const [tooltipPosition, setTooltipPosition] = useState({ x: 0, y: 0 });

  // Group hospitals by city
  const hospitalsByCity = hospitals.reduce((acc, hospital) => {
    const city = hospital.city;
    if (!acc[city]) acc[city] = [];
    acc[city].push(hospital);
    return acc;
  }, {} as Record<string, Hospital[]>);

  // Get color based on hospital count
  const getCityColor = (city: string) => {
    const cityHospitals = hospitalsByCity[city] || [];
    const count = cityHospitals.length;
    
    if (count === 0) return '#e5e7eb'; // gray-200 - no hospitals
    if (count === 1) return '#3b82f6'; // blue-500 - 1 hospital
    if (count <= 3) return '#10b981'; // green-500 - 2-3 hospitals
    return '#f59e0b'; // orange-500 - 4+ hospitals
  };

  const handleCityMouseEnter = (city: string, event: React.MouseEvent) => {
    setHoveredCity(city);
    const rect = (event.target as SVGElement).getBoundingClientRect();
    setTooltipPosition({ 
      x: rect.left + rect.width / 2, 
      y: rect.top 
    });
    
    const cityHospitals = hospitalsByCity[city] || [];
    onCityHover?.(city, cityHospitals);
  };

  const handleCityMouseLeave = () => {
    setHoveredCity(null);
  };

  const handleCityClick = (city: string) => {
    const cityHospitals = hospitalsByCity[city] || [];
    onCityClick?.(city, cityHospitals);
  };

  return (
    <div className="relative w-full max-w-6xl mx-auto">
      <div className="bg-gradient-to-br from-blue-50 to-indigo-100 rounded-xl p-6 shadow-lg">
        <svg 
          viewBox="0 0 800 350" 
          className="w-full h-auto"
          style={{ aspectRatio: '800/350' }}
        >
          {/* Render each city with its actual SVG path */}
          {Object.entries(TURKEY_CITIES_SVG).map(([cityName, pathData]) => {
            const cityHospitals = hospitalsByCity[cityName] || [];
            const hospitalCount = cityHospitals.length;
            const cityColor = getCityColor(cityName);
            
            return (
              <path
                key={cityName}
                d={pathData}
                fill={cityColor}
                stroke="#ffffff"
                strokeWidth="0.5"
                className={cn(
                  "cursor-pointer transition-all duration-300 hover:brightness-110",
                  hoveredCity === cityName && "brightness-110 drop-shadow-md"
                )}
                onMouseEnter={(e) => handleCityMouseEnter(cityName, e)}
                onMouseLeave={handleCityMouseLeave}
                onClick={() => handleCityClick(cityName)}
                data-city={cityName}
                data-hospitals={hospitalCount}
              />
            );
          })}
        </svg>
      </div>

      {/* Modern Tooltip */}
      {hoveredCity && (
        <div
          className="fixed z-50 bg-black/90 text-white px-4 py-3 rounded-lg text-sm pointer-events-none backdrop-blur-sm"
          style={{
            left: tooltipPosition.x,
            top: tooltipPosition.y - 10,
            transform: 'translate(-50%, -100%)'
          }}
        >
          <div className="font-semibold text-base">{hoveredCity}</div>
          <div className="text-gray-300 text-xs mt-1">
            {hospitalsByCity[hoveredCity]?.length || 0} hastane
          </div>
          {hospitalsByCity[hoveredCity] && hospitalsByCity[hoveredCity].length > 0 && (
            <div className="text-gray-300 text-xs mt-2 max-w-xs">
              {hospitalsByCity[hoveredCity].slice(0, 3).map(h => h.name).join(', ')}
              {hospitalsByCity[hoveredCity].length > 3 && ` ve ${hospitalsByCity[hoveredCity].length - 3} tane daha...`}
            </div>
          )}
        </div>
      )}

      {/* Modern Legend */}
      <div className="mt-6 bg-white rounded-lg p-4 shadow-sm border">
        <h4 className="text-sm font-semibold text-gray-700 mb-3">Hastane Dağılımı</h4>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-3 text-sm">
          <div className="flex items-center gap-2">
            <div className="w-4 h-4 rounded-sm bg-gray-200 border"></div>
            <span className="text-gray-600">Hastane yok</span>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-4 h-4 rounded-sm bg-blue-500 border"></div>
            <span className="text-gray-600">1 hastane</span>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-4 h-4 rounded-sm bg-green-500 border"></div>
            <span className="text-gray-600">2-3 hastane</span>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-4 h-4 rounded-sm bg-orange-500 border"></div>
            <span className="text-gray-600">4+ hastane</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TurkeyMap;